namespace MU\TransportModule\Base;

use Doctrine\DBAL\Connection;
use RuntimeException;
use Zikula\Core\AbstractExtensionInstaller;

/**
 * Installer base class.
 */
abstract class AbstractTransportModuleInstaller extends AbstractExtensionInstaller
{
    /**
     * Install the MUTransportModule application.
     *
     * @return boolean True on success, or false
     *
     * @throws RuntimeException Thrown if database tables can not be created or another error occurs
     */
    public function install()
    {
        $logger = $this->container->get('logger');
    
        // create all tables from according entity definitions
        try {
            $this->schemaTool->create($this->listEntityClasses());
        } catch (\Exception $exception) {
            $this->addFlash('error', $this->__('Doctrine Exception') . ': ' . $exception->getMessage());
            $logger->error('{app}: Could not create the database tables during installation. Error details: {errorMessage}.', ['app' => 'MUTransportModule', 'errorMessage' => $exception->getMessage()]);
    
            return false;
        }
    
        // set up all our vars with initial values
        $this->setVar('tableEntriesPerPage', 10);
        $this->setVar('databaseEntriesPerPage', 10);
        $this->setVar('fieldEntriesPerPage', 10);
        $this->setVar('showOnlyOwnEntries', false);
        $this->setVar('allowModerationSpecificCreatorForTable', false);
        $this->setVar('allowModerationSpecificCreationDateForTable', false);
        $this->setVar('allowModerationSpecificCreatorForDatabase', false);
        $this->setVar('allowModerationSpecificCreationDateForDatabase', false);
        $this->setVar('allowModerationSpecificCreatorForField', false);
        $this->setVar('allowModerationSpecificCreationDateForField', false);
    
        // initialisation successful
        return true;
    }
    
